-- create database
CREATE DATABASE russian_kremlins;

-- create extension
CREATE EXTENSION postgis;

-- create tables
CREATE TABLE kremlins (
    PRIMARY KEY (id),
    id    SERIAL,
    name  TEXT,
    shape GEOMETRY(Polygon, 4326)
);

CREATE TABLE cities (
    PRIMARY KEY (id),
    id    SERIAL,
    shape GEOGRAPHY,
    name  TEXT
);


